import base64
import wave

b64_string = ''


# Converte de volta para bytes:
audio_bytes = base64.b64decode(b64_string)

# Agora você pode salvar os bytes como um arquivo:
# with open("saida/decodificado.mp3", "wb") as f:
    # f.write(audio_bytes)

# Salvar áudio corretamente com cabeçalho WAV
with wave.open("saida/decodificado.wav", mode="wb") as wf:
    wf.setnchannels(1)
    wf.setsampwidth(2)  # 16 bits = 2 bytes
    wf.setframerate(24000)
    wf.writeframes(audio_bytes)